package contest.p197;

import java.util.HashMap;
import java.util.Map;

public class P2 {

    Map<Integer, String> map = new HashMap<>();

    public int numSub(String s) {
        int amount = 0;
        int lastI= 0;
        int oneTimes = 1;
        outer:
        while(true){
            boolean foundAtLeastOne = false;
            while(lastI < s.length()){
                if(!map.containsKey(oneTimes))
                    map.put(oneTimes, build(oneTimes));
                int ret = s.indexOf(map.get(oneTimes),lastI);
                if(!foundAtLeastOne && ret == -1){
                    break outer;
                }else if(ret != -1){
                    lastI = ret+1;
                    amount++;
                    foundAtLeastOne = true;
                }else {
                    break;
                }
            }
            oneTimes++;
            lastI=0;
        }
        return amount;
    }

    private String build(int oneTimes){
        StringBuilder sb = new StringBuilder(oneTimes);
        for(int i = 0; i < oneTimes; i++){
            sb.append('1');
        }
        return sb.toString();
    }


    public static void main(String[] args) {
        System.out.println(new P2().numSub("0110111"));
        System.out.println(new P2().numSub("101"));
        System.out.println(new P2().numSub("111111"));
        System.out.println(new P2().numSub("000"));
        System.out.println(new P2().numSub(""));
    }
}
